column_dsfr <- function(
  width = NULL,
  ...,
  extra_class = NULL
    ) {
  if (is.null(width)) {
    add_width <- ""
  } else {
    if (width != 0) {
      add_width <- paste0("-", width)
    } else {
      add_width <- ""
    }
  }

  class <- paste0("fr-col", add_width)

  layout_dsfr_template(
    html = tagList(
      ...
    ),
    class = class,
    other_class = extra_class
  )
}
